import React, { Component } from 'react';
import {
  AppRegistry,
  TabBarIOS,
  View,
  Text,
  StyleSheet
} from 'react-native';
import { Provider } from 'react-redux';
import { createStore } from 'redux';
import todoApp from './app/reducers';
import Tasks from './app/Tasks';

import theme from './app/themes/darkslateblue';

const store = createStore(todoApp);
const base64Icon = 'data:image/png;base64,';

class IOSApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'tasks',
      notifCount: 0,
      presses: 0,
    };
  }

  _renderContent(color, pageText, num) {
    return (
      <View style={[styles.tabContent, {backgroundColor: color}]}>
        <Text style={styles.tabText}>{pageText}</Text>
        <Text style={styles.tabText}>{num} re-renders of the {pageText}</Text>
      </View>
    );
  }

  render() {
    return (
      <Provider store={store}>
        <TabBarIOS
          unselectedTintColor="white"
          tintColor="yellow"
          barTintColor="darkslateblue">
          <TabBarIOS.Item
            title="Tasks"
            icon={{uri: base64Icon, scale: 3}}
            selected={this.state.selectedTab === 'tasks'}
            onPress={() => {
              this.setState({
                selectedTab: 'tasks',
              });
            }}>
            <Tasks theme={theme}/>
          </TabBarIOS.Item>
          <TabBarIOS.Item
            icon={{uri: base64Icon, scale: 3}}
            renderAsOriginal
            title="Settings"
            selected={this.state.selectedTab === 'settings'}
            onPress={() => {
              this.setState({
                selectedTab: 'settings',
                presses: this.state.presses + 1
              });
            }}>
            {this._renderContent('#21551C', 'Settings', this.state.presses)}
          </TabBarIOS.Item>
        </TabBarIOS>
      </Provider>
    );
  }
}

var styles = StyleSheet.create({
  tabContent: {
    flex: 1,
    alignItems: 'center',
  },
  tabText: {
    color: 'white',
    margin: 50,
  },
});


AppRegistry.registerComponent('ReactNativeTodoList', () => IOSApp);
